    private void jbtnOkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtnOkActionPerformed
        // TODO add your handling code here:
        if (!this.jtxtBirth.getText().equals("")) {
            this.jprogBar.setValue(33);
        }
        if (new String(this.jtxtPwd.getPassword()).length() >= 8) {
            this.jprogBar.setValue(this.jprogBar.getValue() + 33);
        }
        if (this.jtxtLongText.getText().length() >= 100) {
            this.jprogBar.setValue(this.jprogBar.getValue() + 33);
        }
    }//GEN-LAST:event_jbtnOkActionPerformed

